mapper.delete:

DELETE FROM todos WHERE userid = #{userId};
DELETE FROM comments WHERE userid = #{userId};
DELETE FROM likes WHERE userid = #{userId};
DELETE FROM follows WHERE followerid = #{userId} OR followingid = #{userId};
DELETE FROM diarys WHERE userid = #{userId};

DELETE FROM users WHERE id = #{id};

mapper.update:
UPDATE users 
SET nickname = #{nickname}, address = #{address}, height = #{height}, 
weight = #{weight}, age = #{age}, gender = #{gender}, isPublic = #{isPublic}
WHERE id = #{id}
